{
  "query": "mutation RegisterUser($input: UserRegistrationInput!) { registerUser(input: $input) { id email username firstName lastName role } }",
  "variables": {
    "input": {
      "email": "test@example.com",
      "password": "password123",
      "firstName": "John",
      "lastName": "Doe",
      "username": "johndoe"
    }
  }
}

{
  "query": "mutation LoginUser($input: UserLoginInput!) { loginUser(input: $input) { token user { id email username firstName lastName role } } }",
  "variables": {
    "input": {
      "email": "contact@brunomoyse.be",
      "password": "admin"
    }
  }
}

{
  "query": "query { me { id email username firstName lastName phone isActive role } }"
}

{
  "query": "query GetUsers($filter: UserFilter, $limit: Int, $offset: Int) { users(filter: $filter, limit: $limit, offset: $offset) { id email username firstName lastName role isActive } }",
  "variables": {
    "limit": 20,
    "offset": 0
  }
}

{
  "query": "query GetUser($userId: ID!) { user(id: $userId) { id email username firstName lastName phone role isActive } }",
  "variables": {
    "userId": "ffffffff-ffff-ffff-ffff-ffffffffffff"
  }
}

{
  "query": "query GetUserStats($userId: ID!, $period: LeaderboardPeriod) { userStatistics(userId: $userId, period: $period) { totalTournaments totalEarnings averagePosition bestPosition worstPosition pointsEarned } }",
  "variables": {
    "userId": "ffffffff-ffff-ffff-ffff-ffffffffffff",
    "period": "ALL_TIME"
  }
}

{
  "query": "query { clubs { id name city } }"
}

{
  "query": "query GetClub($clubId: ID!) { club(id: $clubId) { id name city } }",
  "variables": {
    "clubId": "01234567-8901-2345-6789-012345678901"
  }
}

{
  "query": "query GetTournaments($clubId: UUID, $status: TournamentStatus, $limit: Int, $offset: Int) { tournaments(clubId: $clubId, status: $status, limit: $limit, offset: $offset) { id title startTime endTime buyInCents status liveStatus maxPlayers currentPlayers club { id name city } } }",
  "variables": {
    "limit": 20,
    "offset": 0
  }
}

{
  "query": "query GetTournament($tournamentId: ID!) { tournament(id: $tournamentId) { id title description startTime endTime buyInCents status liveStatus maxPlayers currentPlayers structure club { id name city } } }",
  "variables": {
    "tournamentId": "01234567-8901-2345-6789-012345678901"
  }
}

{
  "query": "mutation RegisterForTournament($input: RegisterForTournamentInput!) { registerForTournament(input: $input) { id tournamentId userId registeredAt } }",
  "variables": {
    "input": {
      "tournamentId": "01234567-8901-2345-6789-012345678901",
      "userId": "ffffffff-ffff-ffff-ffff-ffffffffffff"
    }
  }
}

{
  "query": "mutation EnterTournamentResults($input: EnterTournamentResultsInput!) { enterTournamentResults(input: $input) { success message resultsEntered } }",
  "variables": {
    "input": {
      "tournamentId": "01234567-8901-2345-6789-012345678901",
      "playerPositions": [
        {
          "userId": "ffffffff-ffff-ffff-ffff-ffffffffffff",
          "position": 1,
          "prizeAmountCents": 50000
        }
      ],
      "playerDeals": []
    }
  }
}

{
  "query": "mutation UpdateTournamentStatus($input: UpdateTournamentStatusInput!) { updateTournamentStatus(input: $input) { id status liveStatus } }",
  "variables": {
    "input": {
      "tournamentId": "01234567-8901-2345-6789-012345678901",
      "status": "IN_PROGRESS"
    }
  }
}

{
  "query": "query GetTournamentClock($tournamentId: ID!) { tournamentClock(tournamentId: $tournamentId) { id tournamentId status currentLevel timeRemaining totalLevels isPaused } }",
  "variables": {
    "tournamentId": "01234567-8901-2345-6789-012345678901"
  }
}

{
  "query": "mutation CreateTournamentClock($tournamentId: ID!) { createTournamentClock(tournamentId: $tournamentId) { id tournamentId status currentLevel timeRemaining totalLevels isPaused } }",
  "variables": {
    "tournamentId": "01234567-8901-2345-6789-012345678901"
  }
}

{
  "query": "mutation StartTournamentClock($tournamentId: ID!) { startTournamentClock(tournamentId: $tournamentId) { id tournamentId status currentLevel timeRemaining totalLevels isPaused } }",
  "variables": {
    "tournamentId": "01234567-8901-2345-6789-012345678901"
  }
}

{
  "query": "mutation PauseTournamentClock($tournamentId: ID!) { pauseTournamentClock(tournamentId: $tournamentId) { id tournamentId status currentLevel timeRemaining totalLevels isPaused } }",
  "variables": {
    "tournamentId": "01234567-8901-2345-6789-012345678901"
  }
}

{
  "query": "mutation ResumeTournamentClock($tournamentId: ID!) { resumeTournamentClock(tournamentId: $tournamentId) { id tournamentId status currentLevel timeRemaining totalLevels isPaused } }",
  "variables": {
    "tournamentId": "01234567-8901-2345-6789-012345678901"
  }
}

{
  "query": "mutation AdvanceTournamentLevel($tournamentId: ID!) { advanceTournamentLevel(tournamentId: $tournamentId) { id tournamentId status currentLevel timeRemaining totalLevels isPaused } }",
  "variables": {
    "tournamentId": "01234567-8901-2345-6789-012345678901"
  }
}

{
  "query": "query GetTournamentStructure($tournamentId: ID!) { tournamentStructure(tournamentId: $tournamentId) { level smallBlind bigBlind ante duration } }",
  "variables": {
    "tournamentId": "01234567-8901-2345-6789-012345678901"
  }
}

{
  "query": "mutation CreateTournamentTable($input: CreateTournamentTableInput!) { createTournamentTable(input: $input) { id tableNumber maxSeats currentSeats tournament { id title } } }",
  "variables": {
    "input": {
      "tournamentId": "01234567-8901-2345-6789-012345678901",
      "tableNumber": 1,
      "maxSeats": 9
    }
  }
}

{
  "query": "mutation AssignPlayerToSeat($input: AssignPlayerToSeatInput!) { assignPlayerToSeat(input: $input) { id seatNumber stackSize player { id firstName lastName } table { id tableNumber } } }",
  "variables": {
    "input": {
      "tableId": "01234567-8901-2345-6789-012345678901",
      "playerId": "ffffffff-ffff-ffff-ffff-ffffffffffff",
      "seatNumber": 1,
      "stackSize": 20000
    }
  }
}

{
  "query": "mutation MovePlayer($input: MovePlayerInput!) { movePlayer(input: $input) { id seatNumber stackSize player { id firstName lastName } table { id tableNumber } } }",
  "variables": {
    "input": {
      "playerId": "ffffffff-ffff-ffff-ffff-ffffffffffff",
      "fromTableId": "01234567-8901-2345-6789-012345678901",
      "toTableId": "01234567-8901-2345-6789-012345678902",
      "toSeatNumber": 3
    }
  }
}

{
  "query": "mutation UpdateStackSize($input: UpdateStackSizeInput!) { updateStackSize(input: $input) { id seatNumber stackSize player { id firstName lastName } } }",
  "variables": {
    "input": {
      "playerId": "ffffffff-ffff-ffff-ffff-ffffffffffff",
      "newStackSize": 15000
    }
  }
}

{
  "query": "mutation BalanceTables($input: BalanceTablesInput!) { balanceTables(input: $input) { id seatNumber stackSize player { id firstName lastName } table { id tableNumber } } }",
  "variables": {
    "input": {
      "tournamentId": "01234567-8901-2345-6789-012345678901"
    }
  }
}

{
  "query": "query GetSeatAssignments($tournamentId: ID, $tableId: ID, $limit: Int, $offset: Int) { seatAssignments(tournamentId: $tournamentId, tableId: $tableId, limit: $limit, offset: $offset) { id seatNumber stackSize player { id firstName lastName } table { id tableNumber } } }",
  "variables": {
    "tournamentId": "01234567-8901-2345-6789-012345678901",
    "limit": 50,
    "offset": 0
  }
}

{
  "query": "query GetTournamentLeaderboard($period: LeaderboardPeriod, $clubId: UUID, $limit: Int, $offset: Int) { tournamentLeaderboard(period: $period, clubId: $clubId, limit: $limit, offset: $offset) { rank user { id firstName lastName } totalPoints totalTournaments totalEarnings averagePosition } }",
  "variables": {
    "period": "ALL_TIME",
    "limit": 20,
    "offset": 0
  }
}

{
  "query": "query GetTournamentPlayers($tournamentId: ID!) { tournamentPlayers(tournamentId: $tournamentId) { id user { id firstName lastName email } registeredAt position prizeAmountCents pointsAwarded } }",
  "variables": {
    "tournamentId": "01234567-8901-2345-6789-012345678901"
  }
}

{
  "query": "query { serverTime }"
}

{
  "query": "query { __schema { types { name description fields { name type { name } } } } }"
}

{
  "query": "mutation { createTournamentClock(tournamentId: \"01234567-8901-2345-6789-012345678901\") { id } }"
}

{
  "query": "mutation { createTournamentClock(tournamentId: \"01234567-8901-2345-6789-012345678901\") { id } }"
}
